package data;

public class Question {
	
	private String question;
	private int difficulty;
	private int id;
	private String [] topics;
	
	
	public Question(String question, int difficulty, int id, String[] topics) {
		this.question = question;
		this.difficulty = difficulty;
		this.id = id;
		this.topics = topics;
	}
	
	public String[] getTopics() {
	   
		return topics;
	}
	public void setTopics(String[] topics) {
		this.topics = topics;
	}
	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public Question() {
		// TODO Auto-generated constructor stub
	}
	public String getQuestion() {
		return question;
	}
	public void setQuestion(String question) {
		this.question = question;
	}
	public int getDifficulty() {
		return difficulty;
	}
	public void setDifficulty(int difficulty) {
		this.difficulty = difficulty;
	}
	//????????????????????????????????????????????????????????????????
	public String toString() {
		return this.question +":"+ this.difficulty;
		
	}
	//????????????????????????????????????????????????????????????????

	public String topicsToString(String [] topics) {
		String topicsStr = String.join(",",topics) ;
		return topicsStr;
	}
	//????????????????????????????????????????????????????????????????

	public String[] topicsToStringArr(String topics) {
		String[] topicsStr = topics.split(",") ;
		this.topics = topicsStr;
		return this.topics;
	}
	

}
